mapper.select:
SELECT w.*, f.freelancer_seq, f.user_seq, f.hope_recruit_type_val, f.hope_work_posi_type_val,  f.main_manager_id, f.seeking_work_status

FROM 	worker w, freelancer f 


LIMIT #{offset}, #{limit}

mapper.sql:
w.worker_seq = f.worker_seq AND w.customer_code = #{customerCode}


	AND w.name = #{name}



	AND w.academic_level_val  >=  #{minAcademicLevelVal}



	AND w.academic_level_val  <=  #{maxAcademicLevelVal}



	AND f.main_manager_id = #{mainManagerId}



	AND w.worker_expert_type = #{workerExpertType}



	AND w.career_start_year  >=  #{workBaseYear}


	AND w.career_start_month  >=  #{workBaseMonth}



	AND EXISTS (SELECT 1 FROM worker_advantage  WHERE w.worker_seq = worker_seq  AND advantage_seq = ${item})
